import java.util.Scanner;
public class DisplayTime2 {
  public static void main(String[] args) {
	//Step 1: Read the milliseconds
	Scanner scan = new Scanner(System.in);
	System.out.println("Enter the milliseconds: ");
	long milliSeconds = scan.nextLong();
	scan.close();
	//Step 2: Obtain hours, minutes and seconds from the milliseconds
    long totalSecs = milliSeconds / 1000;
    long hours = totalSecs / 3600;
    long mins = (totalSecs / 60) % 60;
    long secs = totalSecs % 60;
    //Step 3: Display the result
    System.out.println(milliSeconds+" milliseconds is "+hours + " hours and " + mins + " minutes and " + secs + " seconds.");
  }
}
