import java.util.Scanner;
public class VendingMachine2
{
	public static void main(String[] args)
	{
		//Step 1: Define the constants that we need for this program
		final int PENNIES_PER_DOLLAR = 100;
		final int PENNIES_PER_QUARTER = 25;
		final int PENNIES_PER_DIME = 10;
		final int PENNIES_PER_NICKEL = 5;
		//Step 2: Read the bill value and item price
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter bill value in dollars (1 = $1 bill, 5 = $5 bill, etc.): ");
		int billValue = scan.nextInt();
		System.out.println("Enter item price in pennies: ");
		int itemPrice = scan.nextInt();
		scan.close();
		//Step 3: Compute the change due
		int changeDue = PENNIES_PER_DOLLAR * billValue - itemPrice;
		//Step 4: Compute the number of quarters in the change due and update the change due after taking away the quarters
		int quarters = changeDue / PENNIES_PER_QUARTER;
		changeDue = changeDue % PENNIES_PER_QUARTER;
		//Step 5: Compute the number of dimes in the remaining change due and update the change due after taking away the dimes
		int dimes = changeDue / PENNIES_PER_DIME;
		changeDue = changeDue % PENNIES_PER_DIME;
		//Step 6: Compute the number of nickels in the remaining change due
		int nickels = changeDue / PENNIES_PER_NICKEL;
		//Step 7: Display the result
		System.out.println("Your change consists of:"); 
		System.out.println(quarters + " quarters");
		System.out.println(dimes + " dimes"); 
		System.out.println(nickels + " nickels");
	}
}
