import java.util.Scanner;
public class JPhoneAge2 {
	public static void main(String[] args){
		//Step 1: Read the user inputs
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter whether the phone screen is good (true or false):");
		boolean screenIsGood = scan.nextBoolean();		
		System.out.println("Enter whether the phone is broken (true or false):");
		boolean isBroken = scan.nextBoolean();		
		System.out.println("Enter whether the phone has the random shutdown problem (true or false):");
		boolean randomShutdown = scan.nextBoolean();		
		scan.close();
		//Step 2: Write the boolean expression to determine whether it is time to buy a new phone
		boolean needPhone = (isBroken) || !screenIsGood || (randomShutdown);
		System.out.println(needPhone);
	}
}
