public class TVTester2 {  
	public static void main(String[] args) {
		TV2 tv1 = new TV2();
		tv1.turnOn();
		tv1.setVolume(4);
		tv1.setVolume(-1);
		tv1.volumeDown();
		TV2 tv2 = new TV2();
		tv2.turnOn();
		tv2.volumeUp();
		System.out.println("tv1's volume level is " + tv1.getVolumeLevel());
		System.out.println("tv2's volume level is " + tv2.getVolumeLevel());
	}
}
class TV2 {
	//Step 1: Declare the instance variables 
	private boolean on = false; 
	private int volumeLevel = 1; 
	//Step 2: Define the methods to change the on/off state of the TV 
	public void turnOn() {
		on = true;
	}
	public void turnOff() {
		on = false;
	}
	//Step 3: Define the methods to change the volume level of the TV 
	public void setVolume(int newVolumeLevel) {
		if (on && newVolumeLevel >= 1 && newVolumeLevel <= 7)
			volumeLevel = newVolumeLevel;
	}
	public void volumeUp() {
		if (on && volumeLevel < 7)
			volumeLevel++;
	}
	public void volumeDown() {
		if (on && volumeLevel > 1)
			volumeLevel--;
	}
	//Step 4: Define the methods to get the current state of the TV 
	public int getVolumeLevel() {
		return volumeLevel;
	}
	public boolean isOn() {
		return on;
	}
}
