public class JArrayFillOdds {
	public static void main(String[] args) {
		//Step 1: Create an array of ten elements of type int
		int[] lst = new int[10];
		//Step 2: Iterate over the indexes of the array
		for (int i = 0; i < 10; i++) {
			//Step 2.1: Assign an odd number to the index i of the array
			lst[i] =  2 * i + 1 ;
		}
		//Step 3: Print the array elements
	   for (int x : lst) {
		   System.out.print(x + "  ");
	   }
	}
}
