import java.util.Scanner;
public class JArrayFillUserInput {
	public static void main(String[] args) {
		//Step 1: Create an array of eight elements of type string
		String[] lst = new String[8];
		//Step 2: Iterate over the indexes of the array
		Scanner scan = new Scanner(System.in);
		for (int i = 0; i < 8; i++) {
			//Step 2.1: Assign the user input to the index i of the array
			System.out.println("Enter a value: ");		
			lst[i] = scan.next();
		}
		scan.close();
		//Step 3: Print the array elements
	   for (String x : lst) {
		   System.out.print(x + "  ");
	   }
	}
}
