import java.util.Scanner;
public class JRentCar2 {
	public static void main(String[] args) {
		//Step 1: Read the inputs from the car rental agent
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the customer's age:");
		int age = scan.nextInt();
		System.out.println("Enter whether the customer has driver's license (true or false):");
		boolean hasLicense = scan.nextBoolean();	
		System.out.println("Enter whether the customer has had an accident (true or false):");
		boolean hadAccidents = scan.nextBoolean();		
		scan.close();
		//Step 2: Write the boolean expression to determine whether a customer could rent a car
		boolean canRentCar = age >= 21 && hasLicense && !hadAccidents;
		System.out.println(canRentCar);
	}
}
