public class JStringEqual1 {
	public static void main(String[] args) {
		//Step 1: Define the strings
		String str1 = "HELLO WORLD!";
		String str2 = "hello world!";
		//Step 2: Compare the strings "HELLO WORLD!" and "hello world!" and print the result
		System.out.println(str1.equals(str2));
		//Step 3: Modify the string "HELLO WORLD!" to match the string "Hello Dave!"
		String str3 = "Hello Dave!";
		str1 = str1.replace("HELLO", "Hello");
		str1 = str1.replace("WORLD", "Dave");
		//Step 4: Compare the modified string with the string "Hello Dave!" and print the result
		System.out.println(str1.equals(str3));
	}
}
