public class JAverageArrayElements {
	public static void main(String[] args) {
		//Step 1: Define the data and variable that we need for this program
		int[] lst = {12, 15, 3, 4, 6, 5};
		int total = 0;
		//Step 2: Add the array elements to the running total one by one 
		for (int x : lst)
		{
		    total += x;
		}
		//Step 3: Calculate and print the average 
		if (lst.length == 0) {
			System.out.println("The array has no values.");
		} else {
			double average = (double) total / lst.length;
			System.out.println("Average is: " + average);	
		}
	}
}
