public class JArrayBasic3
{
   public static void main (String[] args)
   {
	  //#Step 1: Define an array of four strings
	   String[] lst = {"morning", "noon", "afternoon", "evening"};
	   //Step 2: Update the array element
	   lst[3] = "midnight";
	   //Step 3: Print the array elements
	   for (String x : lst) {
		   System.out.print(x + "  ");
	   }
   }
}
