public class Initials3
{
	public static void main(String[] args)
	{
		//Step 1: Define the name
		String fullName = "Alice Ann Darcy";
		//Step 2: Extract the initial letter from the first name
		String firstInitial = fullName.substring(0, 1);
		//Step 3: Extract the middle initial
		String middleInitial = fullName.substring(6, 7);
		//Step 4: Extract the last name
		String last = fullName.substring(10, fullName.length());
		//Step 5: Create the APA style name, last name first, followed by initials.
		String name = last + ", " + firstInitial + ". " + middleInitial + ".";
		System.out.println(name);
	}
}
