import java.util.Scanner;
public class JNestedIfTemperature2 {
	public static void main(String[] args) {
		//Step 1: Read the temperature for today and yesterday
		Scanner scan = new Scanner (System.in);
		System.out.println("Enter the yesterday's temperature: ");
		double yesterday = scan.nextDouble();
		System.out.println("Enter the today's temperature: ");
		double today = scan.nextDouble();		
		//Step 2: Read the humidity for today and yesterday
		System.out.println("Enter yesterday's humidity: ");
		double humidityYesterday = scan.nextDouble();
		System.out.println("Enter today's humidity: ");
		double humidityToday = scan.nextDouble();
		scan.close();
		//Step 3: Warn the user about the changes in the temperature
		if ( today < yesterday ) {
			System.out.println("It is getting colder!");
		} else {
			if ( today > yesterday) {
				//Step 3.1: Determine and warn the user about the changes in humidity when it is getting warmer
				if ( humidityToday < humidityYesterday ) {
					System.out.println("It is getting warmer but less humid!");
				} else if ( humidityToday > humidityYesterday ) {
					System.out.println("It is getting warmer and more humid!");
				} else {
					System.out.println("It is getting warmer but humidity has not changed!");
				}
			} else {
				System.out.println("Temperature is the same as yesterday!");
			}
		}
	}
}
