import java.util.Scanner;
public class CelsiusToFahrenheit
{
	public static void main (String[] args)
	{
		//Step 1: Define the data that we need for this program
		final int BASE = 32;
		final double CONVERSION_FACTOR = 9.0 / 5;
		//Step 2: Read the input Celsius value
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the Celsius value: ");
		int celsiusTemp = scan.nextInt();
		scan.close();
		//Step 3: Compute the  Fahrenheit equivalent of the Celsius value
		double fahrenheitTemp = celsiusTemp * CONVERSION_FACTOR + BASE;
		System.out.println("Celsius Temperature: " + celsiusTemp);
		System.out.println("Fahrenheit Equivalent: " + fahrenheitTemp);
	}
}
