import java.util.Scanner;
public class JThreeBoolean1 {
	public static void main(String[] args) {
		//Step 1: Read the user inputs
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the value of the 1st boolean variable (true or false):");
		boolean a = scan.nextBoolean();		
		System.out.println("Enter the value of the 2nd boolean variable (true or false):");
		boolean b = scan.nextBoolean();	
		System.out.println("Enter the value of the 3rd boolean variable (true or false):");
		boolean c = scan.nextBoolean();		
		scan.close();
		//Step 2: Write the boolean expression to determine whether at least one of the three boolean variables is true
		boolean value = a || b || c;
		System.out.println(value);
	}
}
