public class PointTester1 {
	public static void main(String[] args) {
		Point1 point = new Point1();
		point.setX(7);
		point.setY(2);
		point.translate(11, 6);
		System.out.println("The point's coordinates: (" + point.getX() + ", " + point.getY() + ")") ;
	}
}
class Point1 {
	//Step 1: Declare the instance variables
	private int x;
	private int y;
	//Step 2: Declare the method to shift this point's location by the given amount
	public void translate(int dx, int dy) {
		x += dx;
		y += dy;
	}
	//Step 3: Define the setter and getter methods for the x-coordinate of the point
	public void setX(int newX) {
		x = newX;
	}
	public int getX() {
		return x;
	}
	//Step 4: Define the setter and getter methods for the y-coordinate of the point
	public void setY(int newY) {
		y = newY;
	}
	public int getY() {
		return y;
	}
}
