public class PointTester2 {
	public static void main(String[] args) {
		Point2 point = new Point2();
		point.setX(7);
		point.setY(2);
		System.out.println("The point's coordinates: (" + point.getX() + ", " + point.getY() + ")") ;
		System.out.println("Distance of the point from origin = " + point.distanceFromOrigin());
	}
}
class Point2 {
	//Step 1: Declare the instance variables
	private int x;
	private int y;
	//Step 2: Declare the method to calculate and return the point's distance from the origin
	public double distanceFromOrigin() {
		return Math.sqrt(x * x + y * y);
	}
	//Step 3: Define the setter and getter methods for the x-coordinate of the point
	public void setX(int newX) {
		x = newX;
	}
	public int getX() {
		return x;
	}
	//Step 4: Define the setter and getter methods for the y-coordinate of the point
	public void setY(int newY) {
		y = newY;
	}
	public int getY() {
		return y;
	}
}
