import java.util.Scanner;
public class JCheckProductCode1 {
	public static void main(String[] args) {
		JCheckProductCode1 p1 = new JCheckProductCode1();
		p1.checkProductCode();
	}
	//Step 1: Define the method
	public void checkProductCode() {
		//Step 1.1: Define the variables that we need for counts
	    int valid = 0;
	    int banned = 0;
	    //Step 1.2: Ask for a valid input as long as the user does'nt enter STOP; otherwise stop
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter product code:");
		String code = scan.next();	    
	    while ( ! code.equals("STOP") ) {
	    	 //Step 1.2.1: Enclose the code that might throw an exception within the try block
	        try {
	            char zone = code.charAt(9);
	            valid += 1;
	            if (zone == 'R' ) {
	                banned += 1;
	            }
	        }
	        //Step 1.2.2: Handle all possible exceptions that may be thrown in the try block 
	       catch (IndexOutOfBoundsException ex ) {                    
	            System.out.println("Improper code length.");
	       } 
			System.out.println("Enter product code:");
			code = scan.next();
	    }
	    //Step 1.3: Print the result    
	    System.out.println("# of valid codes entered: " + valid);
	    System.out.println("# of banned codes entered: "+ banned);
	}
}
