import java.util.Scanner;
public class JCheckAge1 {
	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the name and age, separated by a colon:");
		String name = scan.next();
		JCheckAge1 jca = new JCheckAge1();
		jca.checkAge(name);
	}
	// Step 1: Define the method that processes the input string
	public void checkAge(String s) {
		//Step 1.1: Split the given string into the name and age 
		String[] lst = s.split(":");
		//Step 1.2: Enclose the code that might throw an exception within the try block
		try {
			String name = lst[0];
			int age = Integer.parseInt(lst[1]);
			if ( age >= 13 && age <= 19 ) {           
				System.out.println(name + " is a teenager.");
			} else {          
				System.out.println(name + " is not a teenager.");
			}
		}
		//Step 1.3: Handle all possible exceptions that may be thrown in the try block 
		catch ( ArrayIndexOutOfBoundsException ex ) {
			System.out.println("Error! Separate the name and age by a colon.");
		} catch ( NumberFormatException ex) {
			System.out.println("Error! Age must be an integer.");
		}
	}
}
