import java.util.Scanner;
public class JCheckAge2 {
	public static void main(String[] args) {
		JCheckAge2 jca = new JCheckAge2();
		jca.checkAge();
	}
	// Step 1: Define the method that processes the input string
	public void checkAge() {
		//Step 1.1: Ask for a valid input as long as the input could not be processed; otherwise stop
		Scanner scan = new Scanner(System.in);
		boolean processed = false;
		while ( ! processed ) {
			System.out.println("Enter the name and age, separated by a colon:");
			String s = scan.next();
			//Step 1.1.1: Split the given string into the name and age 
			String[] lst = s.split(":");
			//Step 1.1.2: Enclose the code that might throw an exception within the try block
			try {
				String name = lst[0];
				int age = Integer.parseInt(lst[1]);
				if ( age >= 13 && age <= 19 ) {           
					System.out.println(name + " is a teenager.");
				} else {          
					System.out.println(name + " is not a teenager.");
				}
	            processed = true;
			}
			//Step 1.1.3: Handle all possible exceptions that may be thrown in the try block 
			catch ( ArrayIndexOutOfBoundsException ex ) {
				System.out.println("Error! Separate the name and age by a colon.");
			} catch ( NumberFormatException ex) {
				System.out.println("Error! Age must be an integer.");
			}
		}	
	}
}
