import java.util.Scanner;
public class JStars1
{
	public static void main (String[] args)
	{		
		//Step 1: Read the number of rows in the right triangle star pattern
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the number of rows in the right triangle star pattern: ");
		int N = scan.nextInt();
		scan.close();
		//Step 2: Print the rows in the star pattern, one by one
		for (int i = 1; i <= N; i++)
		{
			//Step 2.1: Print the asterisks in the i-th row
			for (int j = 1; j <= i; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
	}
}
