public class InheritancePointTester1 {
	public static void main(String[] args) {
		Point p = new Point(1, 2);
	    System.out.println(p);  
	    Point3D p3d = new Point3D(11, 12, 13);
	    System.out.println(p3d);
	}
}
//Step 1: Define the class Point
class Point {
	//Step 1.1: Declare the instance variables
	private int x;
	private int y;
	//Step 1.2: Define the constructor to create a point with the specified xy-coordinate
	public Point(int x, int y) {
		this.x = x;
	    this.y = y;
	}
	//Step 1.3: Define the toString method to get the string representation of the Point instance
	public String toString() {
	    return "(" + x + ", " + y + ")";
	}
	//Step 1.4: Define the getter methods for getting the xy-coordinate of the point
	public int getX() {
		return x;
	}
	public int getY() {
		return y;
	}		
}
//Step 2: Extend the class Point to define the class Point3D
class Point3D extends Point {
	//Step 2.1: Declare the instance variable
	private int z;
	//Step 2.2: Define the constructor to create a 3D point with the specified xyz-coordinates
	public Point3D(int x, int y, int z) {
		super(x, y);
	    this.z = z;
	}
	//Step 2.3: Define the method to get the z-coordinate of the 3D point
	public int getZ() {
		return z;
	}		
	//Step 2.4: Override the toString method of class Point to get the string representation of the Point3D instance
	public String toString() {
	    return "(" + super.getX() + "," + super.getY() + "," + this.z + ")";
	}
}
