public class AnimalTester2 {
	public static void main(String[] args) {
		Animal anim = new Animal("Fido");   
		anim.speak();
		Dog doggy = new Dog("Smokey","Bob"); 
		doggy.speak();
	}
}
//Step 1: Define the class Animal
class Animal {
	//Step 1.1: Declare the instance variable
	private String name;
	//Step 1.2: Define the constructor to create an animal with the specified name
	public Animal(String name) {
		this.name = name;
	}
	 //Step 1.3: Define the method to give a voice to the animal
	public void speak() {
		System.out.println("My name is " + name);
	}
	//Step 1.4: Define the toString method to get the string representation of the Animal instance
	public String toString() {
		return " Name: " + name;
	}
	//Step 1.5: Define the method to get the animal's name
	public String getName() {
		return name;
	}
}
//Step 2: Extend the class Animal to define the class Dog
class Dog extends Animal {
	//Step 2.1: Declare the instance variable
	private String owner;
	//Step 2.2: Define the constructor to create a dog with the specified name and owner
 	public Dog(String name, String owner) {
 		super(name); 
		this.owner = owner;	
	}
	//Step 2.3: Override the speak method of the class Animal to give a voice to the Dog
	public void speak() {
		super.speak();
		System.out.println("Woof, Woof!");
	}
	//Step 2.4: Override the toString method of class Animal to get the string representation of the Dog instance
	public String toString() {
		return super.toString() + ",  Owner: " + owner;
	}
}
