import java.util.Scanner;
public class JTemperatureAboveAverage {
	public static void main(String[] args) {
		//Step 1: Read the number of days that we have to enter their temperature data.
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the number of temperature values that will be entered:");
		int numDays = scan.nextInt();
		//Step 2: Read the temperature values and update the running total
		double[] temps = new double[numDays];
		int total = 0;
		for (int i = 0; i < numDays; i++) {
			System.out.println("Enter the temperature: " );
			temps[i] = scan.nextDouble();
			total += temps[i];
		}
		scan.close();
		//Step 3: Calculate the average temperature
		double average = 0;
		if (numDays == 0) {
			System.out.println("No temperature values were entered.");
		} else {
			average = (double) total / numDays;			
		}
		//Step 4: Count the number of the days that are above the average temperature
		int above = 0;
		for (double x : temps) {
			if (x > average) {
				above++;
			}
		}
		//Step 5: Display the results
		System.out.println("Average = " + average);
		System.out.println(above + " days above average.");
	}
}
