import java.util.Scanner;
public class JTemperatureListDaysAboveThreshold {
	public static void main(String[] args) {
		//Step 1: Read the number of days that we have to enter their temperature data.
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the number of temperature values that will be entered:");
		int numDays = scan.nextInt();
		//Step 2: Read the temperature values and update the running total
		double[] temps = new double[numDays];		
		for (int i = 0; i < numDays; i++) {
			System.out.println("Enter the temperature: " );
			temps[i] = scan.nextDouble();
		}
		scan.close();
		//Step 3: Create an array to store true/false flags to show which days are above 32 degrees Fahrenheit
		boolean[] aboveThreshold = new boolean[numDays];
		for (int i = 0; i < temps.length; i++) {
			if ( temps[i] > 32) {
				aboveThreshold[i] = true;
			}
		}
		//Step 4: Print the result
		System.out.println("Days above 32 degrees Fahrenheit:");
	   for (int i = 0; i < aboveThreshold.length; i++) {
		   if ( aboveThreshold[i] == true ) {
			   System.out.println("Day " + (i + 1)); 
		   }
	   }
	}
}
