import java.util.Scanner;
public class JFailCourse3 {
	public static void main(String[] args) {
		//Step 1: Read the instructor inputs
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the student's score:");
		int studentScore = scan.nextInt();
		System.out.println("Enter the class average:");
		int classAverage = scan.nextInt();
		scan.close();
		//Step 2: Write the boolean expression to determine whether the student fails the course
		boolean isFailing = !( studentScore > classAverage );
		System.out.println(isFailing);
	}
}
