import java.util.Scanner;
public class JAdjacentConsecutives {
	public static void main(String[] args) {
		//Step 1: Read the first integer that the user enters
		System.out.println("Enter the integers separated by spaces or new line (enter -1 to quit): ");
		Scanner scan = new Scanner(System.in); 
		int num = scan.nextInt();
		//Step 2: Read the rest of the integers that the user enters and check for adjacent consecutive numbers
		while (num != -1) {
			int previous = num;
			num = scan.nextInt();
			if ( num != -1 && num - previous == 1 ) {
				System.out.println(previous + " " +num + " are consecutive."); 
			}
		}
		scan.close();
	}
}
