import java.util.Scanner;
public class JAdjacentGreater {
	public static void main(String[] args) {
		//Step 1: Read the first number that the user enters
		System.out.println("Enter the numbers separated by spaces or new line: ");
		Scanner scan = new Scanner(System.in); 
		double num = scan.nextDouble();
		//Step 2: Read the rest of the numbers that the user enters and check for adjacent numbers in ascending order
		while (num != 0) {
			double previous = num;
			num = scan.nextDouble();	
			if ( num != 0 && num > previous ) {
				System.out.println(previous + " " + num + " are in ascending order."); 
			}
		}
		scan.close();
	}
}
