public class JArrayRotateLeft {
	//Step 1: Define the method for rotating the array to the left by 1 position
	public static int[] rotateLeft(int[] lst) {
		//Step 1.1: Store the value at the front in a local variable
		int first = lst[0];
		//Step 1.2: Rotate the remaining values of the array to the left  
		for (int i = 0; i < lst.length - 1; i++) {
			lst[i] = lst[i + 1];
		}
		//Step 1.3: Move the value at the front to the back of the array
		lst[lst.length - 1] = first;
		//Step 1.4: Return the rotated array
		return lst;
	}
	//Step 2: Define the method for printing the array
	public static void print(int[] array) {
		for (int x : array) {
			System.out.print(x + " ");
		}
		System.out.println();
	}
	public static void main(String[] args) {
		int[] values = {3, 8, 9, 8, 7, 5};
		System.out.println("Original Array:");
		print(values);
		System.out.println("Rotated Array:"); 
		print(rotateLeft(values));
	}
}
