public class JArrays2dBasic2 {
	public static void main(String[] args) {
		//Step 1: Define a 3x3 two-dimensional array 
		int[][] matrix = {{ 1, 2, 3 },
				          { 4, 5, 6 },
				          { 7, 8, 9}};
		//Step 2: Update the two-dimensional array 
		matrix[0][2] = 10;
		//Step 3: Iterate over the rows of the matrix
		for (int i = 0; i < matrix.length; i++) {
			//Step 3.1: Print the elements in the row i
			for (int j = 0; j <  matrix[0].length; j++) {
				System.out.print(matrix[i][j] + " ");
		    }
		    System.out.println();
		}
	}
}
