import java.util.Scanner;
public class JBooleanDryHot4 {
	public static void main(String[] args) {
		//Step 1: Read the user inputs
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter whether it is too hot (true or false):");
		boolean tooHot = scan.nextBoolean();	
		System.out.println("Enter whether it is too dry (true or false):");
		boolean tooDry = scan.nextBoolean();		
		scan.close();
		//Step 2: Write the boolean expression to determine whether it is either too hot or too dry but not both.
		boolean value1 = (tooHot || tooDry) && !(tooHot && tooDry);
		boolean value2 = tooHot ^ tooDry;
		System.out.println(value1);
	}
}
