public class JSodaSurverySodaMax {
	public static void main(String[] args) {
		//Step 1: Define a 4x10 two-dimensional array
		int[][] ratingsData = {{ 3, 4, 5, 2, 1, 4, 3, 2, 4, 4 }, 
				{ 2, 4, 3, 4, 3, 3, 2, 1, 2, 2 },
				{ 3, 5, 4, 5, 5, 3, 2, 5, 5, 5 },
				{ 1, 1, 1, 3, 1, 2, 1, 3, 2, 4 }};
		//Step 2: Get the dimensions of the array that stores the ratings data
		int numSodas = ratingsData.length;
		int numRespondents = ratingsData[0].length;
		//Step 3: Create an array to store the maximum rating of each soda flavor
		int[] sodaMax = new int[numSodas];
		//Step 4: Iterate through the sodas 
		for (int i = 0; i < numSodas; i++) {
			//Step 4.1: Find the maximum rating of soda i+1 by iterating through its ratings 
			for (int j = 0; j < numRespondents; j++) {
				if ( ratingsData[i][j] > sodaMax[i]) {
					sodaMax[i] = ratingsData[i][j];
				}
			}
		}
		//Step 5: Print the maximum rating for each soda
		System.out.println("Maximums:");
		for (int i = 0; i < numSodas; i++) {
			System.out.printf("Soda # %d : %d\n", i+1, sodaMax[i]);
		}
	}
}
