public class JSodaSurverySodaRespondentAvg {
	public static void main(String[] args) {
		//Step 1: Define a 4x10 two-dimensional array
		int[][] ratingsData = {{ 3, 4, 5, 2, 1, 4, 3, 2, 4, 4 }, 
				{ 2, 4, 3, 4, 3, 3, 2, 1, 2, 2 },
				{ 3, 5, 4, 5, 5, 3, 2, 5, 5, 5 },
				{ 1, 1, 1, 3, 1, 2, 1, 3, 2, 4 }};
		//Step 2: Get the dimensions of the array that stores the ratings data
		int numSodas = ratingsData.length;
		int numRespondents = ratingsData[0].length;
		//Step 3: Create an array to store the sum of rating of each soda flavor
		int[] sodaSum = new int[numSodas];
		//Step 4: Create an array to store the sum of rating of each respondent
		int[] respondentSum = new int[numRespondents];
		//Step 5: Iterate through the sodas 
		for (int i = 0; i < numSodas; i++) {
			//Step 5.1: Iterate through the respondents
			for (int j = 0; j < numRespondents; j++) {
				//Step 5.1.1: Update sum of ratings given to soda i+1
				sodaSum[i] += ratingsData[i][j];
				//Step 5.1.2: Update sum of ratings from respondent j+1
				respondentSum[j] += ratingsData[i][j];
			}
		}
		//Step 6: Calculate and print the average rating for each soda
		System.out.println ("Averages:");
		for (int i = 0; i < numSodas; i++) {
			System.out.printf("Soda # %d : %.2f\n", (i+1), (double) sodaSum[i]/numRespondents);
		}
		//Step 7: Calculate and print the average rating for each respondent    
		for (int j = 0; j < numRespondents; j++) {
			System.out.printf("Respondent # %d : %.2f\n", (j+1), (double) respondentSum[j]/numSodas);
		}
	}
}
