public class JArrayMin {
	public static void main(String[] args) {
		//Step 1: Define and initialize the array
		int[] values = {5, 8, 4, 78, 95, 12, 1, 0, 6, 35, 46};
		//Step 2: Set the first value to be the minimum value so far.
		int minValue = values[0];
		//Step 3: Iterate through the remaining values in the array and decide which one is the minimum value 
		for (int i = 1; i < values.length; i++) {
			if (values[i] < minValue) {
				minValue = values[i];
			}
		}
		//Step 4: Print the minimum value in the array
		System.out.println("Minimum value: " + minValue);
	}
}
