public class JArrayMax {
	public static void main(String[] args) {
		//Step 1: Define and initialize the array
		int[] values = {5, 8, 4, 78, 95, 12, 1, 0, 6, 35, 46};
		//Step 2: Set the first value to be the maximum value so far.
		int maxValue = values[0];
		//Step 3: Iterate through the remaining values in the array and decide which one is the maximum value 
		for (int i = 1; i < values.length; i++) {
			//Step 3.1: Determine if the element at index i is the maximum value so far
			if (values[i] > maxValue) {
				maxValue = values[i];
			}
		}
		//Step 4: Print the maximum value in the array
		System.out.println("Maximum value: " + maxValue);
	}
}
