#Paso 1: Definir la función que crea el diccionario
def crear_diccionario(lista_estudiantes, lista_puntajes):
    #Paso 1.1: Crear un diccionario vacío 
    dicc_estudiante_ptjes={}
    #Paso 1.2: Actualizar las entradas del diccionario mientras iteramos a través de los índices en las listas dadas
    for i in range(len(lista_estudiantes)):
        if lista_estudiantes[i] in dicc_estudiante_ptjes:
            dicc_estudiante_ptjes[lista_estudiantes[i]].append(lista_puntajes[i])
        else:
            dicc_estudiante_ptjes[lista_estudiantes[i]] = [lista_puntajes[i]]
    return dicc_estudiante_ptjes
#Paso 2: Definir la función que calcula el promedio de las puntajes de cada estudiante
def promedio(nuevo_dicc):
    #Paso 2.1: Iterar a través de los estudiantes en el diccionario
    for nombre_est in nuevo_dicc:
        #Paso 2.1.1: Iterar a través de la lista de puntajes del estudiante, sumar cada puntuación al total acumulado
        total = 0;
        for puntaje in nuevo_dicc[nombre_est]:
            total += puntaje
        #Paso 2.1.2: Calcular e imprimir el promedio de los puntajes del estudiante
        promedio = total / len(nuevo_dicc[nombre_est])
        print(nombre_est, promedio)
#Paso 3: Llamar a las funciones
nombres = ['Jordan', 'Tom', 'Barbara', 'Susana', 'Sandra', 'Jordan', 'Sandra']
puntajes = [10, 23, 13, 18, 12, 9, 15]
promedio(crear_diccionario(nombres, puntajes))
