#Paso 1: Definir la función
def informar_horas_trabajadas(nombre_archivo):
    #Paso 1.1: Encerrar el código que podría lanzar una excepción dentro del bloque try
    try :
        #Paso 1.1.1: Abrir el archivo y procesar cada línea en el archivo        
        mi_archivo = open( nombre_archivo, "r")
        for linea in mi_archivo:
            tokens = linea.split()
            nombre = tokens[0]
            total = 0.0;
            for i in range(1, len(tokens)) :
                try :
                    total += float(tokens[i])
                except ValueError:
                    print("Error en la hora.")
            print("Total de horas trabajadas por " + nombre  + " = " + str(total))
        #Paso 1.1.2: Cerrar el archivo
        mi_archivo.close()
    #Paso 1.2: Manejar todas las posibles excepciones que pueden ser lanzadas en el bloque try 
    except FileNotFoundError:
        print("Archivo no encontrado")
    except IOError:
        print("¡Problema con el archivo!")
#Paso 2: Llamar a la función
nombre = input("Ingrese la ruta completa de un archivo: ")
informar_horas_trabajadas(nombre)
