#Paso 1: Definir la función
def crear_diccionario(s):
    #Paso 1.1: Crear un diccionario vacío
    diccionario_res = {}
    #Paso 1.2: Dividir la cadena dada en palabras
    s = s.lower()
    palabras = s.split()
    #Paso 1.3: Actualizar las entradas del diccionario mientras iteramos a través de las palabras
    for palabra in palabras:
        if palabra[0] in diccionario_res:
            if palabra not in diccionario_res[palabra[0]]:
                diccionario_res[palabra[0]].append(palabra)
        else:
            diccionario_res[palabra[0]] = [palabra]
    #Paso 1.4: Imprimir el diccionario
    for caracter in diccionario_res:
        print(caracter, ":", diccionario_res[caracter])
#Paso 2: Llamar a la función
s = input("Introduce una cadena: ")
crear_diccionario(s)
