public class SimpleLinearRegression {

    // Sample training data
    static double[] x = {1, 2, 3, 4, 5};
    static double[] y = {2, 4, 6, 8, 10};  // y = 2x

    // Hypothesis function: y = mx + b
    static double m = 0;  // slope
    static double b = 0;  // intercept

    public static void main(String[] args) {
        double learningRate = 0.01;
        int iterations = 1000;

        for (int i = 0; i < iterations; i++) {
            gradientDescentStep(learningRate);
        }

        System.out.println("Final model: y = " + m + "x + " + b);
        predict(6); // Predict y for x = 6
    }

    static void gradientDescentStep(double lr) {
        double dm = 0;
        double db = 0;
        int n = x.length;

        for (int i = 0; i < n; i++) {
            double prediction = m * x[i] + b;
            double error = prediction - y[i];
            dm += error * x[i];
            db += error;
        }

        // Update m and b
        m -= lr * dm / n;
        b -= lr * db / n;
    }

    static void predict(double input) {
        double output = m * input + b;
        System.out.println("Predicted value for x = " + input + " is y = " + output);
    }
}
