public class JRecursionPower1{
    static int power(int a, int b) {
        if(b > 0){
            return a * power(a, b-1);
        }
        return 1;
    }
    public static void main(String args[]){
        JRecursionPower1 rp = new JRecursionPower1();
        int a = 2, b=8;
        System.out.println("The 8th power of 2 is: " + rp.power(a,b));
    }

}
