public class JRecursionSum1{
    int sum1toN(int n){
        if(n > 0){
            return n + sum1toN(n-1);
        }
        return 0;  
    }

    public static void main(String[] args) {
        JRecursionSum1 rs = new JRecursionSum1();
        int n = 10;
        System.out.println("Sum of 10 Numbers is: "+rs.sum1toN(n));
    }
}
