public class JDecInc2 {
	public static void main(String[] args) {
		//Step 1: Define the variables
		int a = 8; 
		int b = 3;
		//Step 2: Perform the operations and print the first output
		System.out.println( a++ + --b ); 
		//Step 3: Perform the operations and print the second output
		System.out.println( ++a + b++ ); 
	}
}
