public class JCharAt2 {
	public static void main(String[] args) {
		//Step 1: Extract the characters from the string
		String str = "Second Example!"; 
		char ch1 = str.charAt(6);
		char ch2 = str.charAt(10);
		char ch3 = str.charAt(13);
		//Step 2: Print the extracted characters
		System.out.println("The 7th character: "+ch1);
		System.out.println("The 11th character: "+ch2);
		System.out.println("The 14th character: "+ch3);
	}
}
