public class Initials2
{
	public static void main(String[] args)
	{
		//Step 1: Define the name
		String fullName = "John Phillip Smith";
		//Step 2: Extract the first name
		String first = fullName.substring(0, 4);
		//Step 3: Extract the middle initial
		String middleInitial = fullName.substring(5, 6);
		//Step 4: Extract the last name
		String last = fullName.substring(13, fullName.length());
		//Step 5: Create the name from the first name, middle initial, and last name
		String name = first + " " + middleInitial + ". " + last;
		System.out.println(name);
	}
}
