import java.io.*;
import java.util.Scanner;
public class JWorkHours2 {
	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the name of the file:");
		String name = scan.next();
		JWorkHours2 jwh = new JWorkHours2();
		jwh.reportWorkHours(name);
	}
	//Step 1: Define the method that processes the employees file
	public void reportWorkHours(String fileName) {
		//Step 1.1: Enclose the code that might throw an exception within the try block
		try {
			//Step 1.1.1: Wrap a BufferedReader around a FileReader
			FileReader fr = new FileReader(fileName); 
			BufferedReader br = new BufferedReader(fr); 
			//Step 1.1.2: Use the BufferedReader object to read one line at a time
			String line = null;
			while ( ( line = br.readLine() ) != null) {
				String[] tokens = line.split(" ");
				//Try to get the id of the employee as a number
				try {
					int eid =  Integer.parseInt(tokens[0]);
				} catch ( NumberFormatException ex) {
					System.out.println("Error in the employee's id.");
				}
				String name = tokens[1];
				double total = 0.0;
				for (int i = 2; i < tokens.length; i++ ) {
					try {
						total += Double.parseDouble(tokens[i]);
					} catch (  NumberFormatException ex ) {
						System.out.println("Error in the hour.");
					}
				}
				System.out.println("Total hours worked by " + name + " = " + total);
			} 	
			//Step 1.1.3: Close the BufferedReader object attached to the file
			br.close();
		}
		//Step 1.2: Handle all possible exceptions that may be thrown in the try block 
		catch (FileNotFoundException ex) {
			System.out.println("File not found");
		}  catch (IOException ex ) {
			System.out.println("Problem with the file!");
		}
	}
}
