public class Initials
{
	public static void main(String[] args)
	{
		//Step 1: Define the name
		String fullName = "John Smith";
		//Step 2: Extract the initial letter from the first name
		String firstInitial = fullName.substring(0, 1);
		//Step 3: Extract the initial letter from the last name
		String lastInitial = fullName.substring(5, 6);
		//Step 4: Create the initials from the initial letter in the first and the last name
		String initials = firstInitial + lastInitial;
		//Step 5: Print the initials
		System.out.println(initials);
	}
}
