/* Fig. 4.7: fig04_07.c
   Counting letter grades */
#include <stdio.h>

/* function main begins program execution */
int main( void )
{
   int grade;
   int aCount = 0;
   int bCount = 0;
   int cCount = 0;
   int dCount = 0;
   int fCount = 0;

   printf(  "Enter the letter grades.\n"  );
   printf(  "Enter the EOF character to end input.\n"  );

   /* loop until user types end-of-file key sequence */
   while ( ( grade = getchar() ) != EOF ) {

      /* determine which grade was input */
      switch ( grade ) {

         case 'A':
         case 'a':
            ++aCount;
            break;

         case 'B':
         case 'b':
            ++bCount;
            break;

         case 'C':
         case 'c':
            ++cCount;
            break;

         case 'D':
         case 'd':
            ++dCount;
            break;

         case 'F':
         case 'f':
            ++fCount;
            break;

         case '\n':
         case '\t':
         case ' ':
            break;

         default:
            printf( "Incorrect letter grade entered." );
            printf( " Enter a new grade.\n" );
            break;
      }

   }

   /* output summary of results */
   printf( "\nTotals for each letter grade are:
" );
   printf( "A: %d\n", aCount );
   printf( "B: %d\n", bCount );
   printf( "C: %d\n", cCount );
   printf( "D: %d\n", dCount );
   printf( "F: %d\n", fCount );

   return 0;

}
