	{
		//Step 1: Define the constants that we need for this program
		final int PENNIES_PER_DOLLAR = 100;
		final int PENNIES_PER_QUARTER = 25;
		final int PENNIES_PER_DIME = 10;
		final int PENNIES_PER_NICKEL = 5;
		//Step 2: Read the bill value and item price
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter bill value in dollars (1 = $1 bill, 5 = $5 bill, etc.): ");
		int billValue = scan.nextInt();
		System.out.println("Enter item price in pennies: ");
		int itemPrice = scan.nextInt();
		scan.close();
		//Step 3: Compute the change due
		int changeDue = PENNIES_PER_DOLLAR * billValue - itemPrice;
		//Step 4: Compute the number of quarters in the change due and update the change due after taking away the quarters
		int quarters = changeDue / PENNIES_PER_QUARTER;
		changeDue = changeDue % PENNIES_PER_QUARTER;
		//Step 5: Compute the number of dimes in the remaining change due and update the change due after taking away the dimes
		int dimes = changeDue / PENNIES_PER_DIME;
		changeDue = changeDue % PENNIES_PER_DIME;
		//Step 6: Compute the number of nickels in the remaining change due
		int nickels = changeDue / PENNIES_PER_NICKEL;
		//Step 7: Display the result
		System.out.println("Your change consists of:"); 
		System.out.println(quarters + " quarters");
		System.out.println(dimes + " dimes"); 
		System.out.println(nickels + " nickels");
	}
}

public class JSearchArrayValues {
	//Step 1: Define the method
	public static void searchArrays(double[] lst1, double[] lst2) {
		//Step 1.1: Iterate through the values in the 2nd array
		for (double val2 : lst2) {
			//Step 1.1.1: Print the value in the 2nd array if it exists in the 1st array
			for (double val1 : lst1) {
				if (val1 == val2) {
					System.out.println(val2 + " exists in both array.");   
				}
			}
		}
	}
	public static void main(String[] args) {
		double[] values1 = {2.0, 11, 4, 5, 3, 3.5, 4, 10, 16, 3};
		double[] values2 =  {7, 11, 3, 1.0};
		searchArrays(values1, values2);
	}
}

public class JSearchArrayTotalCounts {
	//Step 1: Define the method 
	public static void searchArrays(double[] lst1, double[] lst2) {
		//Step 1.1: Iterate through the values in the 2nd array
		int count = 0;
		for (double val2 : lst2) {
			//Step 1.1.1: Iterate through the values in the 1st array
			for (double val1 : lst1) {
				//Step 1.1.1.1: Increment the number of matches if we find a match
				if (val1 == val2) {
					count += 1;
				}
			}
		}
		System.out.println("Total number of times the elements in the 2nd list appear in the 1st list is " + count);   
	}
	public static void main(String[] args) {
		double[] values1 = {2.0, 11, 4, 5, 3, 3.5, 4, 10, 16, 3};
		double[] values2 =  {7, 11, 3, 1.0};
		searchArrays(values1, values2);
	}
}

public class JSearchArrayCountsEach {
	//Step 1: Define the method
	public static void searchArrays(double[] lst1, double[] lst2) {
		//Step 1.1: Create an array that we need to store the number of matches
		int[] counts = new int[lst2.length];
		//Step 1.2: Iterate through the values in the 2nd array
		for (int i = 0; i < lst2.length; i++)
		{
			//Step 1.2.1: Iterate through the values in the 1st array
			for (double val1 : lst1)
			{
				//Step 1.2.1.1: Increment the number of matches for val2 if we find a match for that
				if (lst2[i] == val1)
				{
					counts[i] += 1;
				}
			}
		}
		//Step 1.3: Print the result
		for (int i = 0; i < lst2.length; i++)
			System.out.println("Total matches for " + lst2[i] + ": " + counts[i]);
	}
	public static void main(String[] args) {
		double[] values1 = {2.0, 11, 4, 5, 3, 3.5, 4, 10, 16, 3};
		double[] values2 =  {7, 11, 3, 1.0};
		searchArrays(values1, values2);
	}
}

