import java.util.Scanner;
public class PythagoreanTheorem1 {
	public static void main(String[] args) {
		//Step 1: Receive the values for each side of a right-angle triangle
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the length of side A:");
		double sideA = scan.nextDouble();
		System.out.println("Enter the length of side B:");
		double sideB = scan.nextDouble();
		scan.close();
		//Step 2: Calculate square of side A
		double squareSideA = Math.pow(sideA, 2);
		//Step 3: Calculate square of side B
		double squareSideB = Math.pow(sideB, 2);
		//Step 4: Use Pythagorean theorem to calculate the length of the triangle's hypotenuse
		double hypotenuse = Math.sqrt(squareSideA + squareSideB);
		//Step 5: Print the result
		System.out.println("Given that side A is " + sideA + " and side B is " + sideB + ", the hypotenuse is " + hypotenuse);
	}
}

import java.util.Scanner;
public class PythagoreanTheorem2 {
	public static void main(String[] args) {
		//Step 1: Receive the values for the first side of a right-angle triangle and the hypotenuse
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the length of side A:");
		double sideA = scan.nextDouble();
		System.out.println("Enter the length of the hypotenuse:");
		double hypotenuse = scan.nextDouble();
		scan.close();
		//Step 2: Calculate square of side A
		double squareSideA = Math.pow(sideA, 2);
		//Step 3: Calculate square of hypotenuse
		double squareHypotenuse = Math.pow(hypotenuse, 2);
		//Step 4: Use Pythagorean theorem to calculate the length of the triangle's other adjacent side
		double sideB = Math.sqrt(squareHypotenuse - squareSideA);
		//Step 4: Print the result
		System.out.println("Given that side A is " + sideA + " and the hypotenuse is " + hypotenuse + ", side B is " + sideB);
	}
}

public class JSmallestDivisor {
	public static void main(String[] args) {
		//Step 1: Define the variables that we need for this program
		int num = 15;
		int divisor = 2;
		//Step 2: Find the smallest divisor of the number
		while (num % divisor != 0) {
			divisor += 1;
		}
		System.out.println("The smallest divisor of " + num + " is " + divisor);
	}
}

public class JLargestDivisor {
	public static void main(String[] args) {
		//Step 1: Define the variables that we need for this program
		int num = 15;
		int divisor = num-1;
		//Step 2: Find the largest divisor of the number		
		while ( num % divisor != 0 ) {
			divisor -= 1;
		}
		System.out.println("The largest divisor of " + num + " is " + divisor);
	}
}

