class TV1 {
	//Step 1: Declare the instance variables 
	private boolean on = false; 
	private int channel = 1; 
	//Step 2: Define the methods to change the on/off state of the TV 
	public void turnOn() {
		on = true;
	}
	public void turnOff() {
		on = false;
	}
	//Step 3: Define the methods to change the channel of the TV 
	public void setChannel(int newChannel) {
		if (on && newChannel >= 1 && newChannel <= 120)
			channel = newChannel;
	}
	public void channelUp() {
		if (on && channel < 120)
			channel++;
	}
	public void channelDown() {
		if (on && channel > 1)
			channel--;
	}
	//Step 4: Define the methods to get the current state of the TV 
	public int getChannel() {
		return channel;
	}
	public boolean isOn() {
		return on;
	}
}

