public class JArraySwapAdjacentElements {
	public static void main(String[] args) {
		//Step 1: Define and initialize the array
	   int[] lst = { 1, 2, 3, 4, 5, 6 };
	   //Step 2: Iterate through the pairs of adjacent elements in the array
	   for (int i = 0; i < lst.length; i += 2) {
		    //Step 2.1: Swap the element at index i with the element at index i+1
		    int temp = lst[i];
		    lst[i] = lst[i+1];
		    lst[i+1] = temp;
	   }
		//Step 3: Print the array elements
